import React, {useEffect} from 'react'
import {Link, useParams, useNavigate, useSearchParams} from 'react-router-dom'
import {useDispatch, useSelector} from 'react-redux'
import {Row, Col, ListGroup, Image, Button, Card, Form, ListGroupItem } from 'react-bootstrap'
import Message from '../components/Message'
import {addToCart, removeFromCart} from '../actions/cartActions'

function CartScreen() {
    const navigate = useNavigate(); 
    const {id} = useParams();
    const[searchParams, setSearchParams]= useSearchParams(); 
    const qty = Number(searchParams.get('qty'));
    const dispatch = useDispatch();
    const cart = useSelector((state) => state.cart); 
    const { cartItems }  = cart;  

    const userLogin = useSelector((state) => state.userLogin)
    const { userInfo } = userLogin


    useEffect( () => {
        if (id) {
            dispatch(addToCart(id,qty)); 
        }
    }, [dispatch, id, qty]); 

    const  removeFromCartHandler = (id) => {
        dispatch(removeFromCart(id))
    }

    const checkOutHandler = () => {
        if (!userInfo){
            navigate('/login') 
        }else{
            navigate('/shipping')
        }; 
    }

    return (
        <Row> 
            <Col md={8}>
                <h1> Shopping Cart</h1>
                {cartItems.length === 0 ? (
                    <Message variant='info'>
                        Your cart is empty <Link to='/'> Go Back</Link>
                    </Message>
                ): (
                    <ListGroup variant='flush'>
                        {cartItems.map(item => (
                            <ListGroup.Item key ={item.product}>
                                <Row> 
                                    <Col md={2}> 
                                        <Image src={item.image} alt={item.name} fluid rounded/>
                                    </Col>
                                    <Col md={3}>
                                        <Link to= {`/product/${item.product}`} > {item.name} </Link>
                                    </Col>
                                    <Col md ={2}>
                                         ${item.price}
                                    </Col>

                                    <Col md={3}>
                                        <Form.Control
                                                as = "select"
                                                value={item.qty}
                                                onChange={(e)=> dispatch(addToCart(Number(item.product), Number(e.target.value)))}
                                            >
                                                {

                                                    [...Array(item.countInStock).keys()].map((x) => (
                                                        <option key={x+1} value={x+1}>
                                                            {x + 1}
                                                        </option>
                                                    ))
                                                }
                                            </Form.Control>
                                    </Col>


                                    <Col md={1}>
                                        <Button
                                            type='button'
                                            cariant = 'light'
                                            onClick = {() => removeFromCartHandler(item.product)}
                                        >
                                            <i className='fas fa-trash'> </i>  
                                        </Button>

                                    </Col>

                                </Row>
                            </ListGroup.Item>

                        ))}

                    </ListGroup>
                )

                }
            </Col>

            <Col md={4}>
                <Card>
                    <ListGroup variant='flush'> 
                        <ListGroup.Item>  
                            <h2> Subtotal ({cartItems.reduce((acc, item) => acc + item.qty, 0)}) items </h2>
                            ${cartItems.reduce((acc, item) => acc + item.qty * item.price, 0).toFixed()}
                        </ListGroup.Item>  
                    </ListGroup>  

                    <ListGroup> 
                        <Button
                            type='button'
                            className = 'btn-block'
                            disabled={cartItems.length === 0}
                            onClick = {checkOutHandler}
                        >
                            Proceed to Checkout 
                        </Button>
                    </ListGroup>    

                </Card>
            </Col>
        </Row>  
    );

}

export default CartScreen;
