import * as constants from '../constants/productConstants'


export const productListReducer = (state = {products: []}, action) => {
    switch(action.type){
        case constants.PRODUCT_LIST_REQUEST: 
            return {loading: true, products:[]}

        case constants.PRODUCT_LIST_SUCCESS: 
            return {loading: false, 
                products: action.payload.products, 
                page: action.payload.page, 
                pages: action.payload.pages}

        case constants.PRODUCT_LIST_FAILED: 
            return {loading: false, error: action.payload}

        default:  
            return state 
    } 
}

export const productDetailReducer = (state = {product: {reviews:[]}}, action) => {
    switch(action.type){
        case constants.PRODUCT_DETAILS_REQUEST: 
            return {loading: true, ...state}

        case constants.PRODUCT_DETAILS_SUCCESS: 
            return {loading: false, product: action.payload}

        case constants.PRODUCT_DETAILS_FAILED: 
            return {loading: false, error: action.payload}

        default:  
            return state 
    } 
}

export const productDeleteReducer = (state = {}, action) => {
    switch(action.type){
        case constants.PRODUCT_DELETE_REQUEST: 
            return {loading: true }

        case constants.PRODUCT_DELETE_SUCCESS: 
            return {loading: false, success:true}

        case constants.PRODUCT_DELETE_FAILED: 
            return {loading: false, error: action.payload}

        default:  
            return state 
    } 
}


export const productCreateReducer = (state = {}, action) => {
    switch(action.type){
        case constants.PRODUCT_CREATE_REQUEST: 
            return {loading: true }

        case constants.PRODUCT_CREATE_SUCCESS: 
            return {loading: false, success:true, product: action.payload}

        case constants.PRODUCT_CREATE_FAILED: 
            return {loading: false, error: action.payload}

        case constants.PRODUCT_CREATE_RESET: 
            return { }


        default:  
            return state 
    } 
}


export const productUpdateReducer = (state = {product:{}}, action) => {
    switch(action.type){
        case constants.PRODUCT_UPDATE_REQUEST: 
            return {loading: true }

        case constants.PRODUCT_UPDATE_SUCCESS: 
            return {loading: false, success:true, product: action.payload}

        case constants.PRODUCT_UPDATE_FAILED: 
            return {loading: false, error: action.payload}

        case constants.PRODUCT_UPDATE_RESET: 
            return { product:{}}


        default:  
            return state 
    } 
}

export const productReviewCreateReducer = (state = {}, action) => {
    switch(action.type){
        case constants.PRODUCT_CREATE_REVIEW_REQUEST: 
            return {loading: true }

        case constants.PRODUCT_CREATE_REVIEW_SUCCESS: 
            return {loading: false, success:true}

        case constants.PRODUCT_CREATE_REVIEW_FAILED: 
            return {loading: false, error: action.payload}

        case constants.PRODUCT_CREATE_REVIEW_RESET: 
            return { }


        default:  
            return state 
    } 
}


export const productTopRatedReducer = (state = {products:[]}, action) => {
    switch(action.type){
        case constants.PRODUCT_TOP_REQUEST: 
            return {loading: true, products:[] }

        case constants.PRODUCT_TOP_SUCCESS: 
            return {loading: false, products: action.payload }

        case constants.PRODUCT_TOP_FAILED: 
            return {loading: false, error: action.payload}


        default:  
            return state 
    } 
}
