 import * as cons from '../constants/userConstants'


export  const userLoginReducer = (state = {}, action) => {
    switch(action.type){
        case cons.USER_LOGIN_REQUEST: 
            return {loading: true }

        case cons.USER_LOGIN_SUCCESS: 
            return {loading: false, userInfo: action.payload}

        case cons.USER_LOGIN_FAILED: 
            return {loading: false, error: action.payload}

        case cons.USER_LOGOUT: 
            return {}

        default:  
            return state 
    } 
}

export  const userRegisterReducer = (state = {}, action) => {
    switch(action.type){
        case cons.USER_REGISTER_REQUEST: 
            return {loading: true }

        case cons.USER_REGISTER_SUCCESS: 
            return {loading: false, userInfo: action.payload}

        case cons.USER_REGISTER_FAILED: 
            return {loading: false, error: action.payload}

        case cons.USER_LOGOUT: 
            return {}

        default:  
            return state 
    } 
}

export  const userDetailsReducer = (state = {user: {}}, action) => {
    switch(action.type){
        case cons.USER_DETAILS_REQUEST: 
            return {...state, loading: true }

        case cons.USER_DETAILS_SUCCESS: 
            return {loading: false, user: action.payload}

        case cons.USER_DETAILS_FAILED: 
            return {loading: false, error: action.payload}

        case cons.USER_DETAILS_RESET: 
            return {user: {}}

        default:  
            return state 
    } 
}

export  const userUpdateProfileReducer = (state = {}, action) => {
    switch(action.type){
        case cons.USER_UPDATE_PROFILE_REQUEST: 
            return {loading: true }

        case cons.USER_UPDATE_PROFILE_SUCCESS: 
            return {loading: false, SUCCESS: true, userInfo: action.payload}

        case cons.USER_UPDATE_PROFILE_FAILED: 
            return {loading: false, error: action.payload}

        case cons.USER_UPDATE_PROFILE_RESET: 
            return {}

        default:  
            return state 
    } 
}

export  const userListReducer = (state = {users:[]}, action) => {
    switch(action.type){
        case cons.USER_LIST_REQUEST: 
            return {loading: true }

        case cons.USER_LIST_SUCCESS: 
            return {loading: false, users: action.payload}

        case cons.USER_LIST_FAILED: 
            return {loading: false, error: action.payload}

        case cons.USER_LIST_RESET: 
            return {users:[]}

        default:  
            return state 
    } 
}

export  const userDeleteReducer = (state = { }, action) => {
    switch(action.type){
        case cons.USER_DELETE_REQUEST: 
            return {loading: true }

        case cons.USER_DELETE_SUCCESS: 
            return {loading: false, success:true}

        case cons.USER_DELETE_FAILED: 
            return {loading: false, error: action.payload}


        default:  
            return state 
    } 
}

export  const userUpdateReducer = (state = {user: {}}, action) => {
    switch(action.type){
        case cons.USER_UPDATE_REQUEST: 
            return {loading: true }

        case cons.USER_UPDATE_SUCCESS: 
            return {loading: false, success:true}

        case cons.USER_UPDATE_FAILED: 
            return {loading: false, error: action.payload}

        case cons.USER_UPDATE_RESET: 
            return {user:{}}

        default:  
            return state 
    } 
}
